//import Button from "./Button";
//import styles from "./App.module.css";
import { useState, useEffect } from "react";

function App() {
  const [loading, setLoading] = useState(true);
  const [coins, setCoins] = useState([]);
  const [coinPrice, setCoinPrice] =useState(0);
  const [dollors, setDollors] = useState(0);

  const onChangeCoin = (event) => {
    setCoinPrice(event.target.value);
  }

  const onChangeDollor = (event) => {
    setDollors(event.target.value);
  }


  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
      .then((response) => response.json())
      .then((json) => {
        setCoins(json);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <h1>The Coins! {loading ? null : coins.length}</h1>
      {loading ? (
        <strong>Loading...</strong>
      ) : (
        <div>
          Dollors i have : <input type="number" value={dollors} onChange={onChangeDollor}/>          
          <br />
          I can buy : {need/cost}
          <br />
          <select onChange={onChangeCoin}>
            {coins.map((coin, idx) => (
              <option key={idx} id={coins.symbol}>
                {coin.name}({coin.symbol}) : {coin.quotes.USD.price}
              </option>
            ))}
          </select>
        </div>
      )}
    </div>
  );
}

export default App;
